import React, { Component } from 'react'


class Counter extends Component {

    state = {
        count: 0,
        text:"Like?"
      };

      handleClick = () => {
        this.setState(({ count }) => ({
          count: count + 1,
          text: "Very Much"
        }));
      };

      onmouseover = () => {
        this.setState(({ text }) => ({
        text: "Like?"
        }));
      };



    render() {

        return (

<button class ="comic" onmouseover={this.state.text}onClick={this.handleClick}>{this.state.count}{this.state.text}</button>


        )

    }
}

export default Counter;
