import React, {Component} from 'react'




class Like extends Component {
    constructor(props){
        super(props)

        this.state = {
             text: "Like",
             count:0
          }

      }

      handleClick = () => {
        this.setState(({ count }) => ({
            count: count + 1,
            text: "Very Much",

        }));
    };


render() {


    return (
        <div>

<button className = "comic" onClick={this.props.handleClick}>{this.setState.count}{this.state.text}
 </button>

        </div>
    )
}


}

export default Like;
